import React, { useState, useEffect } from "react";
import { Navigation } from "./components/navigation";
import { Header } from "./components/header";
import { Features } from "./components/features";
import { About } from "./components/about";
import { Adoption } from "./components/adoption";
import AdoptionPage from "./components/AdoptionPage";
import { Gallery } from "./components/gallery";
import { Testimonials } from "./components/testimonials";
import { Team } from "./components/Team";
import { Contact } from "./components/contact";
import JsonData from "./data/data.json";
import SmoothScroll from "smooth-scroll";
import "./App.css";

export const scroll = new SmoothScroll('a[href*="#"]', {
  speed: 1000,
  speedAsDuration: true,
});

const App = () => {
  const [landingPageData, setLandingPageData] = useState({});
  const [currentPage, setCurrentPage] = useState("home");
  useEffect(() => {
    setLandingPageData(JsonData);
  }, []);

  const navigateTo = (target) => {
    // if target is the adoption page, open the separate AdoptionPage
    if (target === "#adoption-page" || target === "adoption-page") {
      setCurrentPage("adoptionPage");
      // update hash for bookmark
      window.location.hash = "#adoption-page";
      return;
    }

    // otherwise go to home and scroll to target section
    setCurrentPage("home");
    // small delay to ensure DOM updated
    setTimeout(() => {
      const el = document.querySelector(target);
      if (el) scroll.animateScroll(el);
    }, 50);
  };

  return (
    <div>
      <Navigation onNavigate={navigateTo} />
      {currentPage === "home" ? (
        <>
          <Header data={landingPageData.Header} />
          <Features data={landingPageData.Features} />
          <About data={landingPageData.About} />
          <Adoption onViewMore={() => navigateTo("#adoption-page")} />
          <Gallery data={landingPageData.Gallery} />
          <Testimonials data={landingPageData.Testimonials} />
          <Team data={landingPageData.Team} />
          <Contact data={landingPageData.Contact} />
        </>
      ) : (
        <AdoptionPage />
      )}
    </div>
  );
};

export default App;
